import React from 'react';
import ListItem from './StyledTags';

const frontSkillsArr = [
  'React.js',
  'JavaScript (ES6)',
  'HTML5',
  'CSS',
  'Bootstrap',
  'Material UI',
  'jQuery'
];

const backSkillsArr = [
  'Node.js',
  'Express.js',
  'Passport.js',
  'Authentication',
  'SQL',
  'MySQL',
  'Sequelize.js',
  'MongoDB',
  'Firebase',
  'Heroku'
];

const styles = {
  rootDiv: {},
  title: {
    fontSize: '3rem',
    fontWeight: '900',
    margin: '2vw'
  },
  section: {
    fontSize: '2rem',
    fontWeight: '900'
  },
  p: {
    fontSize: '1.5rem',
    fontWeight: '600'
  }
};

const Skills = () => {
  return (
    <div style={styles.rootDiv}>
      <div class="row">
        <div class="col-12">
          <h1 style={styles.title}>Technical Skills</h1>
        </div>
      </div>
      <div class="row justify-content-center">
        <div class="col-6">
          <h3 style={styles.section}>Front</h3>
          <ul>
            {frontSkillsArr.map((skill, index) => {
              return <ListItem key={index}>{skill}</ListItem>;
            })}
          </ul>
        </div>
        <div class="col-6">
          <h3 style={styles.section}>Back</h3>
          <ul>
            {backSkillsArr.map((skill, index) => {
              return <ListItem key={index}>{skill}</ListItem>;
            })}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Skills;
